mapper.select:
SELECT dong_id
FROM dong
WHERE dong_name LIKE CONCAT('%', #{dongName}, '%');

mapper.insert:
INSERT INTO academy
   SET user_id = #{userId}
     , dong_id = #{dongId}
     , aca_name = #{acaName}
     , aca_phone = #{acaPhone}
     , comment = #{comment}
     , teacher_num = #{teacherNum}
     , open_time = #{openTime}
     , close_time = #{closeTime}
     , address = #{address}
     , aca_pic = #{acaPic}

mapper.update:
UPDATE academy


        dong_id = #{dongId}


        , aca_name = #{acaName}


        , aca_phone = #{acaPhone}


        , comment = #{comment}


        , teacher_num = #{teacherNum}


        , open_time = #{openTime}


        , close_time = #{closeTime}


        , aca_pic = #{acaPic}


        , address = #{address}



WHERE aca_id = #{acaId}
AND user_id = #{userId}

mapper.select:
SELECT address
FROM academy
WHERE aca_id = #{acaId}

mapper.delete:
DELETE FROM academy
 WHERE aca_id = #{acaId}
   AND user_id = #{userId}

mapper.select:
SELECT A.aca_id, A.aca_phone, A.aca_name, A.aca_pic, A.COMMENT, A.teacher_num, A.open_time, A.close_time, address
FROM academy A
WHERE aca_id = #{acaId}

SELECT street_id, street_name
FROM street
WHERE city_id = #{cityId}

SELECT A.dong_id, A.dong_name
FROM dong A
JOIN street B
ON A.street_id = B.street_id
WHERE B.street_id = #{streetId}
AND city_id = #{cityId};

SELECT A.aca_id, A.aca_name, A.created_at, A.aca_agree
FROM academy A
WHERE user_id = #{signedUserId}

SELECT A.aca_id AS acaId, A.aca_name AS acaName, A.aca_pic AS acaPic, A.address AS address, IFNULL(GROUP_CONCAT(DISTINCT F.tag_name ORDER BY F.tag_id SEPARATOR ', '), '') AS tagName, COALESCE(G.star, 0) AS star, COUNT(*) OVER() AS totalCount FROM academy A LEFT JOIN academytag E ON A.aca_id = E.aca_id LEFT JOIN tag F ON E.tag_id = F.tag_id LEFT JOIN ( SELECT ROUND(AVG(R.star), 2) AS star, C.aca_id FROM review R JOIN class C ON R.join_class_id = C.class_id GROUP BY C.aca_id ) G ON G.aca_id = A.aca_id WHERE 1 = 1 AND (#{dongId} IS NULL OR A.dong_id = #{dongId}) AND (#{searchName} IS NULL OR A.aca_name LIKE CONCAT('%', #{searchName}, '%'))
AND ( SELECT 1 FROM academytag E2 JOIN tag F2 ON E2.tag_id = F2.tag_id WHERE E2.aca_id = A.aca_id AND F2.tag_name = #{tagName})
AND ( A.aca_id IN (SELECT CL.aca_id FROM class CL JOIN classcategory CC ON CL.class_id = CC.class_id WHERE CC.category_id IN
#{CId}
))
GROUP BY A.aca_id ORDER BY G.star DESC LIMIT #{startIdx}, #{size}
