mapper.select:
SELECT dong_id
FROM dong
WHERE dong_name LIKE CONCAT('%', #{dongName}, '%');

mapper.insert:
INSERT INTO academy
   SET user_id = #{userId}
     , dong_id = #{dongId}
     , aca_name = #{acaName}
     , aca_phone = #{acaPhone}
     , comment = #{comment}
     , teacher_num = #{teacherNum}
     , open_time = #{openTime}
     , close_time = #{closeTime}
     , address = #{address}
     , aca_pic = #{acaPic}

mapper.update:
UPDATE academy


        dong_id = #{dongId}


        , aca_name = #{acaName}


        , aca_phone = #{acaPhone}


        , comment = #{comment}


        , teacher_num = #{teacherNum}


        , open_time = #{openTime}


        , close_time = #{closeTime}


        , aca_pic = #{acaPic}


        , address = #{address}


WHERE aca_id = #{acaId}
AND user_id = #{userId}

mapper.select:
SELECT address
FROM academy
WHERE aca_id = #{acaId}

mapper.delete:
DELETE FROM academy
 WHERE aca_id = #{acaId}
   AND user_id = #{userId}

mapper.select:
SELECT A.aca_id, A.aca_phone, A.aca_name, A.aca_pic, A.COMMENT, A.teacher_num, A.open_time, A.close_time, address
FROM academy A
WHERE aca_id = #{acaId}

SELECT street_id, street_name
FROM street
WHERE city_id = #{cityId}

SELECT A.dong_id, A.dong_name
FROM dong A
JOIN street B
ON A.street_id = B.street_id
WHERE B.street_id = #{streetId}
AND city_id = #{cityId};

SELECT A.aca_id, A.aca_name, A.created_at
FROM academy A
WHERE user_id = #{signedUserId}
