mapper.select:
SELECT COUNT(*)
FROM review r
JOIN join_class j ON r.join_class_id = j.join_class_id
WHERE j.user_id = #{userId}
AND r.review_id IN

    #{reviewId}

SELECT COUNT(*)
FROM join_class
WHERE join_class_id = #{joinClassId};

SELECT COUNT(*)
FROM join_class
WHERE class_id = #{classId}
AND user_id = #{userId}
AND certification = 1;

mapper.insert:
INSERT INTO review (join_class_id, comment, star)
VALUES (
(SELECT j.join_class_id
FROM join_class j
WHERE j.user_id = #{userId}
AND j.class_id IN (
SELECT c.class_id
FROM aca_class c
WHERE c.aca_id = #{acaId}
)
LIMIT 1),
#{comment},
#{star}
)

mapper.update:
UPDATE review
SET comment = #{comment}, star = #{star}
WHERE review_id = (
SELECT review_id
FROM review r
JOIN join_class j ON r.join_class_id = j.join_class_id
WHERE j.class_id IN (
SELECT class_id
FROM aca_class
WHERE aca_id = #{acaId}
)
AND j.user_id = #{userId}
ORDER BY r.review_id DESC
LIMIT 1
);

mapper.select:
SELECT COUNT(*) FROM review r
JOIN join_class j ON r.join_class_id = j.join_class_id
JOIN aca_class c ON j.class_id = c.class_id
WHERE c.aca_id = #{acaId}
AND j.user_id = #{userId}

mapper.delete:
DELETE r
FROM review r
JOIN join_class j ON r.join_class_id = j.join_class_id
JOIN aca_class c ON j.class_id = c.class_id
WHERE r.review_id = #{reviewId}
AND c.aca_id = #{acaId}

DELETE FROM review
WHERE review_id = #{reviewId}
AND user_id = #{userId};

mapper.select:
SELECT COUNT(*)
FROM aca_class
WHERE class_id = #{classId};

SELECT COUNT(*)
FROM review
WHERE review_id = #{reviewId};

SELECT COUNT(*)
FROM academy a
JOIN user u ON a.user_id = u.user_id
WHERE a.aca_id = #{acaId}
AND u.user_id = #{userId}
AND u.user_role = 3;

SELECT COUNT(*)
FROM review r
JOIN join_class j ON r.join_class_id = j.join_class_id
WHERE r.join_class_id = #{joinClassId}
AND j.class_id IN (
SELECT class_id
FROM aca_class
WHERE aca_id = #{acaId}
);

SELECT COUNT(*)
FROM academy
WHERE aca_id = #{acaId}

SELECT COUNT(*)
FROM user
WHERE user_id = #{userId}

SELECT COUNT(*)
FROM academy a
JOIN user u ON a.user_id = u.user_id
WHERE u.user_id = #{userId}
AND u.role_id = 3

    AND a.aca_id = #{acaId}

SELECT j.join_class_id
FROM join_class j
JOIN aca_class c ON j.class_id = c.class_id
WHERE c.aca_id = #{acaId}
AND j.user_id = #{userId}
AND certification = 1;

SELECT r.review_id
FROM review r
JOIN join_class j ON r.join_class_id = j.join_class_id
JOIN aca_class c ON j.class_id = c.class_id
WHERE c.aca_id = #{acaId}
AND j.user_id = #{userId}
AND r.join_class_id IN (
SELECT j2.join_class_id
FROM join_class j2
WHERE j2.user_id = #{userId}
);

SELECT COUNT(*)
FROM join_class
WHERE class_id IN

    #{classId}

AND user_id = #{userId}
AND certification = 1;
